//创建投票界面
import axios from "axios"
import { useNavigate } from "react-router-dom"
import { produce } from "immer"
import { useInput } from "./hooks"
import { useBooleanInput } from "./hooks"
import { useState } from "react"
//use-immer是对useState的简单包装。不必再调produce，可以对任意数据类型进行修改
//传给setOption的函数就是用于修改的函数§
// import { set } from "immer/dist/internal"
export default function CreateVote(){
  var [options,setOptions] = useState(['',''])
  var title = useInput()
  var desc = useInput()
  var anonymous = useBooleanInput()
  var deadline  =  useInput()
  var navigate = useNavigate()

  function remove(idx){
    setOptions(
      produce( options,options =>{
        options.splice(idx,1)
      }
    ))
  }

  function setOption(idx,val){
    setOptions(
      produce(options,options =>{
        options[idx] = val
      }
    ))
    console.log(options)
  }

 async function create(){
    var vote = {
      title:title.value,
      desc:desc.value,
      options:options,
      deadline:deadline.value,
      anonymous:anonymous.value
    }
    try{
      var res =  await axios.post('/create-vote',vote)
      var createdVote = res.data.result//data里有创建好的投票信息
      navigate("/vote/"+createdVote.id)
    }catch(e){
      throw e
    }
  }

  return(
    <div>
      <h1>创建投票</h1>
      <div><input type="text" placeholder="投票标题" {...title}/></div>
      <div><input type="text" placeholder="补充描述(选填)" {...desc}/></div>

      {
        options.map((option, idx) =>
          <div key={idx}>
            <input type="text" placeholder="选项" value={option} onChange={e => setOption(idx, e.target.value)}/>
            <button tabIndex="-1" onClick={() => remove(idx)}>-</button>
          </div>
        )
      }
      <div><button onClick={() => setOptions(options => [...options,''])}>添加选项</button></div>
      <div>截止日期:<input type="datetime-local" {...deadline} /></div>
      <div>匿名投票:<input type="checkbox" {...anonymous} /></div>
      <div><button onClick={create}>创建投票</button></div>
    </div>
  )
}
