//创建投票界面
import { useImmer } from "use-immer"
import { useInput } from "./hooks"
import { useBooleanInput } from "./hooks"
//use-immer是对useState的简单包装。不必再调produce，可以对任意数据类型进行修改
//传给setOption的函数就是用于修改的函数§
// import { set } from "immer/dist/internal"
export default function CreateVote(){
  var [options,setOptions] = useImmer(['',''])
  var title = useInput()
  var desc = useInput()
  var isAnonymous = useBooleanInput()
  var deadline  =  useInput()
  function remove(idx){
    setOptions(options,options =>{
        options.splice(idx,1)
      }
    )
  }

  function setOption(idx,val){
    setOptions(options,options =>{
        options[idx] = val
      }
    )
    console.log(options)
  }

  return(
    <div>
      <div>
        <h1>创建投票</h1>
        <div><input type="text" placeholder="投票标题" {...title}/></div>
        <div><input type="text" placeholder="补充描述" {...desc}/></div>
        {
          options.map((option,idx)=>
            <div key={idx}>
              <input type="text"  value={option} onChange={e=>setOption(idx,e.target.value)}/>
              <button onClick={()=>remove(idx)}>-</button>
              </div>
          )
        }
        {/* <div><input type="text" placeholder="选项"/><button>-</button></div>
        <div><input type="text" placeholder="选项"/><button>-</button></div> */}
        <div><button onClick={()=>{setOptions(options=>{options.push('')})}}>添加选项</button></div>
        {/* useimmer 要么修改新的要么返回新的 */}
      </div>
      <div> 截止日期:<input type="date" {...deadline} /></div>
      <div>匿名投票:<input type="checkbox" {...isAnonymous} /></div> 
      <div><button>创建投票</button></div>
    </div>
  )
}
