import { useCallback, useState, } from "react"
import{useNavigate}from 'react-router-dom'
import axios from'axios'
function useInput(init=''){
  var [value,setValue] = useState(init)
  var onChange = useCallback(function onChange(e){
      setValue(e.target.value)
  },[])
  return{
    value,onChange
  }
}
export default function Login(){
var name = useInput()
var password = useInput()
var navigate = useNavigate()
async function login(){
var info = {
  name:name.value,
  password:password.value 
}
try{
  //使用axios发请求
    var res= await axios.post('/account/login',info,{
      withCredentials:true,
    })//axios返回的是相应对象
    var data = res.data//响应体在响应对象的data字段上
    console.log(data.msg) 
    navigate(-1)
}catch(e){
//请求成功，但是响应码大于等于400在axios里也算失败，网络原因直接请求失败，也会抛错
alert(data.msg)
}
}


  return(
    <div>
      <div>Account:</div>
      <input type="text" {...name}/>
      <div>Secret:</div>
      <input type="password" {...password}/>
    <div><button onClick={login}>Login</button></div>
    </div>
  )
}
