//创建投票界面

import { useImmer } from "use-immer"


//use-immer是对useState的简单包装。不必再调produce，可以对任意数据类型进行修改
//传给setOption的函数就是用于修改的函数§
// import { set } from "immer/dist/internal"
export default function CreateVote(){
  var [options,setOptions] = useImmer(['',''])
  function remove(idx){
    setOptions(options,options =>{
        options.splice(idx,1)
      }
    )
  }

  function setOption(idx,val){
    setOptions(options,options =>{
        options[idx] = val
      }
    )
    console.log(options)
  }

  return(
    <div>
      <div>
        <h1>创建投票</h1>
        <div><input type="text" placeholder="投票标题"/></div>
        <div><input type="text" placeholder="补充描述"/></div>
        {
          options.map((option,idx)=>
            <div key={idx}>
              <input type="text"  value={option} onChange={e=>setOption(idx,e.target.value)}/>
              <button onClick={()=>remove(idx)}>-</button>
              </div>
          )
        }
        {/* <div><input type="text" placeholder="选项"/><button>-</button></div>
        <div><input type="text" placeholder="选项"/><button>-</button></div> */}
        <div><button onClick={()=>{setOptions([...options,''])}}>添加选项</button></div>
      </div>
    </div>
  )
}
