//注册新用户
import axios from 'axios'
import { useNavigate } from 'react-router-dom'
import {  useInput } from './hooks'


export default function Register(){
var name =useInput()
var password= useInput()
var email = useInput()
var navigate = useNavigate()

async function register(){
  var newUser = {
    name:name.value,
    password:password.value,
    email:email.value
  }
  console.log('new user',newUser)
  try{
    var res = await axios.post('/account/register',newUser)
    navigate('/login',{replace:true})
  }catch(e){
    let data = res.data
    console.log(data)
  }
}

  return(
    <div>
    <div>账户名:</div>
    <input type="text" placeholder='账户名应只包含数字、字母和下划线' {...name} />
    <div>密码:</div>
    <input type="password" placeholder='请输入密码' {...password}/>
    <div>邮箱:</div>
    <input type="text" placeholder='' {...email} />
    <div><button onClick={register}>注册</button></div>
    </div>
  )

