import{useNavigate}from 'react-router-dom'
import axios from'axios'
import { useInput, useUser } from "./hooks"
import { useEffect } from 'react'
import './login.css'
export default function Login(){
var name = useInput()
var password = useInput()
var navigate = useNavigate()
var userInfo = useUser()

useEffect(()=>{
  if(userInfo.data){
    navigate('/main')
  }
})//登陆成功了的话，不必进入该页面。回退

async function login(){
var info = {
  name:name.value,
  password:password.value 
}
try{
  //使用axios发请求
    var res= await axios.post('/account/login',info)//axios返回的是相应对象
    userInfo.update()
    navigate(-1)
}catch(e){
  let data = res.data
//请求成功，但是响应码大于等于400在axios里也算失败，网络原因直接请求失败，也会抛错
alert(data.msg)
}
}


  return(
    <div className='login-box'>
      <div className='box1'>
      <div>用户名:<input type="text" {...name}/></div>
      <div>密码:<input type="password" {...password}/></div>
      </div>
    <div><button onClick={login}>登陆</button></div>
    </div>
  )
}
